import React, { useState } from "react";
import { toast } from "react-hot-toast";
import {v4 as uuid } from "uuid";

function Home() {
  const [roomId, setRoomId] = useState("");
  const [username, setUsername] = useState("");

  const generateRoomId = (e) =>{
    e.preventDefault();
    const id = uuid();
    setRoomId(id);
    toast.success(" Congratulations on generating the Room ID! ðŸŽ‰");
  };

  const joinRoom = () =>{
    if(!roomID || !username){

    }
  }
  return (
    <div className="container-fluid">
      <div className="row justify-content-center align-items-center min-vh-100">
        <div className="col-12 col-md-6">
          <div className="card shadow-sm p-2 mb-5 bg-secondry rounded">
            <div className="card-body text-center bg-dark">
              <img
                className="img-fluid mx-auto d-block"
                src="/images/logo.png"
                alt="v_editor_logo"
                style={{ maxWidth: "140px" }}
              />
              <h4 className="text-light">Enter the Room Id</h4>
              <div className="form-group">
                <input
                value={roomId}
                onChange={(e) => setRoomId(e.target.value)}
                  type="text"
                  className="form-control mb-2"
                  placeholder="Room Id"
                />

                <div className="form-group">
                  <input
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                    type="text"
                    className="form-control mb-2"
                    placeholder="UserName"
                  />
                </div>
                <button onClick={joinRoom} className="btn btn-success btn-lg btn-block">
                  Submit
                </button>
                <p className="mt-3 text-light">
                  Don't have a room id?{" "}
                  <span
                    className="text-success p-2"
                    style={{ cursor: "pointer" }}
                    onClick={generateRoomId}
                  >
                    New Room
                  </span>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
