import React, { useState, useEffect, useRef } from "react";
import Client from "./Client"; // Assuming this component exists
import ace from "ace-builds/src-noconflict/ace";
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/theme-monokai";
import $ from "jquery";
import "./style.css";

function EditorPage() {
  const [clients, setClients] = useState([
    { socketId: 1, username: "Vijay" },
    { socketId: 2, username: "VIJAY" },
  ]);
  const [language, setLanguage] = useState("c");
  const editorRef = useRef(null);

  // Initialize ACE editor and set theme & language
  useEffect(() => {
    const editor = ace.edit("editor");
    editor.setTheme("ace/theme/monokai");
    editor.session.setMode("ace/mode/c_cpp");
    editorRef.current = editor;

    return () => {
      editor.destroy();
    };
  }, []);

  // Handle language change
  useEffect(() => {
    changeLanguage(language);
  }, [language]);

  function changeLanguage(language) {
    const editor = editorRef.current;
    if (editor) {
      if (language === "c" || language === "cpp") {
        editor.session.setMode("ace/mode/c_cpp");
      } else if (language === "php") {
        editor.session.setMode("ace/mode/php");
      } else if (language === "python") {
        editor.session.setMode("ace/mode/python");
      } else if (language === "node") {
        editor.session.setMode("ace/mode/javascript");
      } else if (language === "java") {
        editor.session.setMode("ace/mode/java");
      }
    }
  }

  // Handle code execution
  function executeCode() {
    $.ajax({
      url: "http://localhost:8080/compiler.php", // Replace this with your server-side script URL
      method: "POST",
      data: {
        language: language,
        code: editorRef.current.getSession().getValue(),
      },
      success: function (response) {
        $(".output").text(response);
      },
      error: function (xhr, status, error) { console.error("AJAX Error: ", status, error); },
    });
  }

  return (
    <div className="container-fluid vh-100">
      <div className="row h-100">
        <div
          className="col-md-2 bg-dark text-light d-flex flex-column h-100"
          style={{ boxShadow: "2px 0px 4px rgba(0,0,0,0.1)" }}
        >
          <img
            src="/images/logo.png"
            alt="v_editor_logo"
            className="img-fluid mx-auto"
            style={{ maxWidth: "150px", marginTop: "-25px" }}
          />
          <hr style={{ marginTop: "-1rem" }} />

          <div className="d-flex flex-column overflow-auto">
            {clients.map((client) => (
              <Client key={client.socketId} username={client.username} />
            ))}
          </div>

          <div className="mt-auto">
            <hr />
            <button className="btn btn-success">Copy Room Id</button>
            <button className="btn btn-danger mt-2 mb-10 px-5 btn-block">
              Lost
            </button>
          </div>
        </div>

        <div
          className="col-md-10 bg-light text-light d-flex flex-column h-100"
          style={{ border: "2px dotted black" }}
        >
          <div className="header">V EDITOR</div>
          <div className="control-panel">
            Select Language: &nbsp;
            <select
              id="languages"
              className="languages"
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
            >
              <option value="c">C</option>
              <option value="cpp">C++</option>
              <option value="php">PHP</option>
              <option value="python">Python</option>
              <option value="node">Node JS</option>
              <option value="java">Java</option>
            </select>
          </div>
          <div className="editor" id="editor"></div>

          <div className="d-flex justify-content-end p-2">
            <button
              className="btn btn-success custom-run-btn"
              onClick={executeCode}
            >
              Run
            </button>
          </div>

          <div className="output"></div>
        </div>
      </div>
    </div>
  );
}

export default EditorPage;
